// Em QuestionEasy.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function QuestionEasy() {
  const [question, setQuestion] = useState(null);
  const { level } = useParams();

  useEffect(() => {
    async function fetchQuestion() {
      // Substitua a URL pela URL correta do seu backend
      const response = await fetch(`http://localhost:8000/perguntas/?categoria=sports&nivel=easy&number=${level}`);
      const data = await response.json();
      setQuestion(data[0]); // Ajuste conforme o formato da sua resposta da API
    }

    fetchQuestion();
  }, [level]);


  if (!question) {
    return <div>Carregando pergunta...</div>;
  }

  return (
    <div>
      <h1>Pergunta do NÃ­vel {level}</h1>
      <h2>{question.pergunta}</h2>
      <ul>
        {question.respostas.map((resposta, index) => (
          <li key={index}>{resposta.texto}</li>
        ))}
      </ul>
      {question.imagem && <img src={question.imagem} alt="Imagem da pergunta" />}
    </div>
  );
}

export default QuestionEasy;
