import React from 'react';
import { Link } from 'react-router-dom';

const categories = ["Sports", "Geography", "History", "Entertainment", "Art", "Science"];

function Categories({ isAuthenticated }) {
  console.log('Autenticado:', isAuthenticated); // Isto é apenas para depuração

  return (
    <div>
      <h1>Categorias</h1>
      {categories.map(category => (
        <button key={category}>{category}</button>
      ))}

      {isAuthenticated && (
        <div className="bottom-nav">
          <Link to="/profile">Perfil</Link>
          <Link to="/stats">Estatísticas</Link>
          {/* Aqui, você precisará de uma maneira de deslogar, semelhante ao que está em App.tsx */}
        </div>
      )}
    </div>
  );
}

export default Categories;
