// Stats.js
import React from 'react';
import '../css/Stats.css'; // Importe seu arquivo CSS específico para esta página
import '../css/BottomNav.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faChartBar, faSignOutAlt } from '@fortawesome/free-solid-svg-icons';
import { Link } from 'react-router-dom';

function Stats() {
  // Crie um array com informações dos jogadores
  const playersInfo = [
    { id: 1, name: "José Valé", score: 120, avatar: "https://cdn-icons-png.flaticon.com/512/149/149071.png" },
    { id: 2, name: "André Pires", score: 110, avatar: "https://cdn-icons-png.flaticon.com/512/149/149071.png" },
    { id: 3, name: "MManuel", score: 100, avatar: "https://cdn-icons-png.flaticon.com/512/149/149071.png" },
    { id: 4, name: "Ana", score: 100, avatar: "https://cdn-icons-png.flaticon.com/512/149/149071.png" },
    // ... outros jogadores
  ];

  // Ordenar jogadores por pontuação, em ordem decrescente
  const sortedPlayers = playersInfo.sort((a, b) => b.score - a.score);

  return (
    <div className="stats-container">
      <header className="leaderboard-header">
        <h2>Global Leaderboard</h2>
        <div className="top-players">
          {sortedPlayers.slice(0, 3).map(player => (
            <div key={player.id} className="player-profile">
              <img src={player.avatar} alt={player.name} className="player-avatar" />
              <p className="player-name">{player.name}</p>
            </div>
          ))}
        </div>
      </header>
      
      <div className="view-toggle">
        <button>Day</button>
        <button>Week</button>
      </div>

      <div className="player-list">
        {sortedPlayers.map((player, index) => (
          <div key={player.id} className="player-entry">
            <span className="player-rank">{index + 1}</span>
            <img src={player.avatar} alt={player.name} className="player-avatar" />
            <span className="player-name">{player.name}</span>
            <span className="player-score">{player.score}</span>
          </div>
        ))}
      </div>
      
      <div className="bottom-nav">
        <Link to="/profile">
          <FontAwesomeIcon icon={faUser} />
        </Link>
        <Link to="/stats">
          <FontAwesomeIcon icon={faChartBar} />
        </Link>
        <Link to="/categories">
          <FontAwesomeIcon icon={faSignOutAlt} />
        </Link>
      </div>
    </div>
  );
}

export default Stats;
