// Register.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../css/Register.css'; // Seu arquivo CSS para estilizar esta página

function Register() {
  const navigate = useNavigate();

  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch('http://127.0.0.1:8000/users/', { // Substitua com a URL do seu backend
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          email: email,
          username: username,
          password: password,
          // O campo confirmarpassword não é utilizado aqui. Adicione se necessário
        })
      });

      if (response.ok) {
        // Registro bem-sucedido, redirecione para a página de login
        navigate('/login');
      } else {
        // Tratar erros de registro aqui (ex: exibir mensagem de erro)
      }
    } catch (error) {
      // Tratar erros de rede aqui
    }
  };

  return (
    <div className="register-container">
      <form onSubmit={handleSubmit} className="register-form">
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Registrar</button>
      </form>
    </div>
  );
}

export default Register;
