import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './SportsEasy.css';
import '../../css/BottomNav.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faChartBar, faSignOutAlt, faLock, faUnlock } from '@fortawesome/free-solid-svg-icons';

function SportsEasy() {
    const [currentQuestion, setCurrentQuestion] = useState(null);
    const navigate = useNavigate();
    const [levels, setLevels] = useState([
        { number: 1, stars: 0, unlocked: true },
        { number: 2, stars: 0, unlocked: false },
        // ... outros níveis ...
    ]);

    useEffect(() => {
        const fetchUserScores = async () => {
            const username = localStorage.getItem('username');
            const token = localStorage.getItem('access_token');
            
            if (username && token) {
                try {
                    const response = await fetch(`http://127.0.0.1:8000/scores/${username}`, {
                        headers: {
                            'Authorization': `Bearer ${token}`,
                        }
                    });

                    if (response.ok) {
                        const userScores = await response.json();
                        updateLevelUnlockStatus(userScores);
                    }
                } catch (error) {
                    console.error('Erro ao buscar pontuações do usuário:', error);
                }
            }
        };

        fetchUserScores();
    }, []);

    const updateLevelUnlockStatus = (userScores) => {
        const updatedLevels = levels.map(level => {
            const levelScore = userScores.find(score => score.level === level.number);
            if (levelScore && levelScore.score >= 3) {
                return { ...level, unlocked: true };
            }
            return level;
        });
        setLevels(updatedLevels);
    };

    const openQuestionEasy = (level) => {
        if (level.unlocked) {
            navigate(`/question-easy/${level.number}`);
        } else {
            alert("Nível bloqueado. Obtenha pontuação suficiente para desbloquear.");
        }
    };

    const renderStars = (numStars) => {
        return Array.from({ length: numStars }, (_, i) => <span key={i}>⭐</span>);
    };

    return (
        <div className="sports-easy-container">
            {/* Cabeçalho, renderização dos níveis, container de perguntas, footer, etc... */}
        </div>
    );
}

export default SportsEasy;
