import React from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Importe useNavigate

const categories = ["Sports", "Geography", "History", "Entertainment", "Art", "Science"];

function Categories({ isAuthenticated, onLogout }) { // Adicione a propriedade onLogout
  const navigate = useNavigate();

  // Função para lidar com o logout
  const handleLogout = () => {
    onLogout(); // Chame a função de logout passada como prop
    navigate('/'); // Redirecione o usuário para a página inicial
  };

  console.log('Autenticado:', isAuthenticated); // Isto é apenas para depuração

  return (
    <div>
      <h1>Categorias</h1>
      {categories.map(category => (
        <button key={category}>{category}</button>
      ))}

      
        <div className="bottom-nav">
          <Link to="/profile">Perfil</Link>
          <Link to="/stats">Estatísticas</Link>
          <button onClick={handleLogout}>Deslogar</button>
        </div>
      
    </div>
  );
}

export default Categories;
