import React, { useState, useEffect } from 'react';

function QuestionEasy() {
  const [question, setQuestion] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {

    const categoria = 'sports';
    const nivel = 'easy';

    async function fetchQuestion() {
      try {
        const response = await fetch(`http://127.0.0.1:8000/perguntas/?categoria=${categoria}&nivel=${nivel}`);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setQuestion(data); // Sup√µe-se que a API retorna um objeto de pergunta diretamente
      } catch (error) {
        setError(error.message);
        console.error("Houve um problema ao buscar a pergunta: ", error);
      }
    }
    
    fetchQuestion();
  }, []);

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!question) {
    return <div>Carregando...</div>;
  }

  return (
    <div>
      <h1>{question.categoria.toUpperCase()} - {question.nivel.charAt(0).toUpperCase() + question.nivel.slice(1)}</h1>
      <p>{question.pergunta}</p>
      <ul>
        {question.respostas.map((resposta, index) => (
          <li key={index}>{resposta.texto}</li>
        ))}
      </ul>
      {question.imagem && <img src={question.imagem} alt="Imagem da pergunta" />}
    </div>
  );
}

export default QuestionPage;
