import React from 'react';
import { useNavigate } from 'react-router-dom'; // Certifique-se de importar useNavigate
import './SportsEasy.css';
import '../../css/BottomNav.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faChartBar, faSignOutAlt } from '@fortawesome/free-solid-svg-icons';


function SportsEasy() {
  // Simulação de dados para os níveis
  const levels = [
    { number: 1, stars: 3 },
    { number: 2, stars: 2 },
    { number: 3, stars: 3 },
    { number: 4, stars: 2 },
    { number: 5, stars: 3 },
    { number: 6, stars: 2 },
    { number: 7, stars: 3 },
    { number: 8, stars: 2 },
    { number: 9, stars: 2 },
    // ... complete com os demais níveis
  ];

  const handleProfileClick = () => {
    // Mostrar aviso para iniciar sessão
    alert("Para ter acesso a esta área, inicie sessão.");
  };

  const handleLogoutClick = () => {
      // Perguntar ao usuário se ele tem certeza que quer sair
      const confirmLogout = window.confirm("Tem a certeza que quer sair?");
      if (confirmLogout) {
          // O utilizador confirmou que quer sair
          navigate('/');
      }
      // Se o utilizador clicar em "Não", nada acontece e permanece na página
  };

  const openQuestionEasy = async (levelNumber) => {
    // Aqui você faria a chamada à API para buscar a pergunta
    try {
      const response = await fetch(`http://127.0.0.1:8000/perguntas/?categoria=sports&nivel=easy&number=${levelNumber}`);
      if (!response.ok) {
        throw new Error('Problema ao buscar a pergunta');
      }
      const perguntas = await response.json();
      // Supondo que o backend retorna uma lista, pegue a primeira pergunta
      const pergunta = perguntas[0];
      setCurrentQuestion(pergunta);
    } catch (error) {
      console.error(error);
    }
  };


  const navigateToCategory = (categoryName) => {
    navigate(`/${categoryName.toLowerCase()}`);
  };


  // Função para renderizar as estrelas
  const renderStars = (numStars) => {
    return Array(numStars).fill(null).map((_, i) => <span key={i}>⭐</span>);
  };

  return (
    <div className="sports-easy-container">
      <header className="sports-easy-header">
        <h1>Sports - Easy</h1>
      </header>
      <div className="sports-easy-levels">
        {levels.map((level) => (
          <div key={level.number} className="level-card" onClick={() => openQuestionEasy(level.number)}>
            <div className="level-number">{`Level ${level.number}`}</div>
            <div className="level-stars">{renderStars(level.stars)}</div>
          </div>
        ))}
      </div>
      <footer className="sports-easy-footer">
      <div className="bottom-nav">
          <FontAwesomeIcon icon={faUser} onClick={handleProfileClick} />
          <FontAwesomeIcon icon={faChartBar}  onClick={handleProfileClick} />
          <FontAwesomeIcon icon={faSignOutAlt} onClick={handleLogoutClick} />
        </div>
      </footer>
    </div>
  );
}

export default SportsEasy;
