//#SportsEasy.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './SportsEasy.css';
import '../../css/BottomNav.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faChartBar, faSignOutAlt } from '@fortawesome/free-solid-svg-icons';

function SportsEasy() {
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const navigate = useNavigate();

  const levels = [
    { number: 1, stars: 3 },
    { number: 2, stars: 2 },
    { number: 3, stars: 3 },
    { number: 4, stars: 2 },
    { number: 5, stars: 3 },
    { number: 6, stars: 2 },
    { number: 7, stars: 3 },
    { number: 8, stars: 2 },
    { number: 9, stars: 2 },
    // ... complete com os demais níveis
  ];



  const openQuestionEasy = (levelNumber) => {
    navigate(`/question-easy/${levelNumber}`); // Ajuste o caminho conforme necessário
  };

  const fetchQuestion = async (levelNumber) => {
    try {
      const response = await fetch(`http://127.0.0.1:8000/perguntas/?categoria=sports&nivel=easy&number=${levelNumber}`);
      if (!response.ok) {
        throw new Error('Problema ao buscar a pergunta');
      }
      const data = await response.json();
      if (data && data.length > 0) {
        setCurrentQuestion(data[0]); // Supondo que a API retorna uma lista e pegamos a primeira pergunta
      } else {
        alert("Não foram encontradas perguntas para este nível.");
        setCurrentQuestion(null);
      }
    } catch (error) {
      console.error("Erro ao buscar pergunta:", error);
      alert("Erro ao buscar pergunta.");
    }
  };

  const renderStars = (numStars) => {
    return Array.from({ length: numStars }, (_, i) => <span key={i}>⭐</span>);
  };

  return (
    <div className="sports-easy-container">
      <header className="sports-easy-header">
        <h1>Sports - Easy</h1>
      </header>

      <div className="sports-easy-levels">
        {levels.map((level) => (
          <div key={level.number} className="level-card" onClick={() => fetchQuestion(level.number)}>
            <div className="level-number">{`Level ${level.number}`}</div>
            <div className="level-stars">{renderStars(level.stars)}</div>
          </div>
        ))}
      </div>

      {currentQuestion && (
        <div className="question-container">
          <h2>{currentQuestion.pergunta}</h2>
          <ul>
            {currentQuestion.respostas.map((resposta, index) => (
              <li key={index}>{resposta.texto}</li>
            ))}
          </ul>
          {currentQuestion.imagem && <img src={currentQuestion.imagem} alt="Imagem da pergunta" />}
        </div>
      )}

      <footer className="sports-easy-footer">
        <div className="bottom-nav">
          <FontAwesomeIcon icon={faUser} onClick={() => alert("Para ter acesso a esta área, inicie sessão.")} />
          <FontAwesomeIcon icon={faChartBar} onClick={() => navigate('/stats')} />
          <FontAwesomeIcon icon={faSignOutAlt} onClick={() => {
            if (window.confirm("Tem a certeza que quer sair?")) {
              navigate('/');
            }
          }} />
        </div>
      </footer>
    </div>
  );
}

export default SportsEasy;
