import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './QuestionEasy.css'; // Certifique-se de criar e importar o arquivo CSS correspondente

function QuestionEasy() {
  const [question, setQuestion] = useState(null);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const { level } = useParams();

  useEffect(() => {
    async function fetchQuestion() {
      try {
        const response = await fetch(`http://localhost:8000/perguntas/?categoria=sports&nivel=easy&number=${level}`);
        if (!response.ok) {
          throw new Error('Problema ao buscar a pergunta');
        }
        const data = await response.json();
        setQuestion(data[0]); // Ajuste conforme o formato da sua resposta da API
      } catch (error) {
        console.error("Erro ao buscar pergunta:", error);
      }
    }

    fetchQuestion();
  }, [level]);

  const handleAnswerSelect = (answer) => {
    setSelectedAnswer(answer);
    // Aqui você pode adicionar lógica para verificar a resposta, armazenar a seleção do usuário, etc.
  };

  const goToNextQuestion = () => {
    // Lógica para ir para a próxima pergunta
  };

  if (!question) {
    return <div>Carregando pergunta...</div>;
  }

  return (
    <div className="question-easy-container">
      <header className="question-easy-header">
        <h1>Sports Easy</h1>
        <h2>Level {level} - first question</h2>
      </header>
      
      <div className="question-card">
        <p className="question-text">{question.pergunta}</p>
        <div className="answers-list">
          {question.respostas.map((resposta, index) => (
            <button
              key={index}
              className={`answer-button ${selectedAnswer === resposta.texto ? 'selected' : ''}`}
              onClick={() => handleAnswerSelect(resposta.texto)}
            >
              {resposta.texto}
            </button>
          ))}
        </div>
      </div>

      <footer className="question-footer">
        <button className="next-button" onClick={goToNextQuestion}>Next</button>
      </footer>
    </div>
  );
}

export default QuestionEasy;
