import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './SportsEasy.css';
import '../../css/BottomNav.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faChartBar, faSignOutAlt, faLock, faUnlock } from '@fortawesome/free-solid-svg-icons';


function SportsEasyLoggedIn() {

    const saveScore = async (level, score) => {
        const username = localStorage.getItem('username');
        const token = localStorage.getItem('access_token');

        if (!username || !token) {
            navigate('/login');
            return;
        }

        try {
            await fetch('http://127.0.0.1:8000/scores', {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ level, score, username })
            });
            // Aqui você pode atualizar o estado para refletir a nova pontuação e desbloquear o próximo nível
        } catch (error) {
            console.error('Erro ao salvar pontuação:', error);
        }
    };




    const [levels, setLevels] = useState([
        { number: 1, stars: 0, unlocked: true },
        { number: 2, stars: 0, unlocked: false },
        // ... adicione o estado inicial para os demais níveis
      ]);
      const navigate = useNavigate();
    
      // Função para navegar para o componente de quiz do nível selecionado
      const openQuestionEasy = (level) => {
        if (level.unlocked) {
          // A rota para o nível do quiz deve ser definida no seu router (não fornecido aqui)
          navigate(`/question-easy/${level.number}`);
        } else {
          // Esta mensagem pode ser personalizada com base na lógica de desbloqueio
          alert("Para jogar os próximos níveis crie conta ou complete os níveis anteriores.");
        }
      };
    
      // Função para renderizar as estrelas com base na pontuação
      const renderStars = (numStars) => {
        return Array.from({ length: numStars }, (_, i) => <span key={i}>⭐</span>);
      };
    
      return (
        <div className="sports-easy-container">
          <header className="sports-easy-header">
            <h1>Sports - Easy</h1>
          </header>
    
          <div className="sports-easy-levels">
            {levels.map((level) => (
              <div key={level.number} className={`level-card ${level.unlocked ? '' : 'locked'}`} onClick={() => openQuestionEasy(level)}>
                <div className="level-number">{`Level ${level.number}`}</div>
                <FontAwesomeIcon icon={level.unlocked ? faUnlock : faLock} className="level-lock-icon" />
                <div className="level-stars">{renderStars(level.stars)}</div>
              </div>
            ))}
          </div>
    
          <footer className="sports-easy-footer">
            <div className="bottom-nav">
              <FontAwesomeIcon icon={faUser} onClick={() => alert("Para ter acesso a esta área, inicie sessão.")} />
              <FontAwesomeIcon icon={faChartBar} onClick={() => navigate('/stats')} />
              <FontAwesomeIcon icon={faSignOutAlt} onClick={() => {
                if (window.confirm("Tem a certeza que quer sair?")) {
                  // Lógica para logout
                  localStorage.clear(); // Isso irá limpar todos os dados locais, incluindo tokens de autenticação
                  navigate('/'); // Navega de volta para a rota inicial ou de login
                }
              }} />
            </div>
          </footer>
        </div>
      );
    }

export default SportsEasyLoggedIn;
