import React from 'react';
import './SportsEasy.css'; // Você precisará criar este arquivo CSS
import { useNavigate, Link } from 'react-router-dom';
import '../c';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faChartBar, faSignOutAlt } from '@fortawesome/free-solid-svg-icons';


function SportsEasy() {
  // Simulação de dados para os níveis
  const levels = [
    { number: 1, stars: 3 },
    { number: 2, stars: 2 },
    { number: 3, stars: 3 },
    { number: 4, stars: 2 },
    { number: 5, stars: 3 },
    { number: 6, stars: 2 },
    { number: 7, stars: 3 },
    { number: 8, stars: 2 },
    { number: 9, stars: 2 },
    // ... complete com os demais níveis
  ];

  // Função para renderizar as estrelas
  const renderStars = (numStars) => {
    return Array(numStars).fill(null).map((_, i) => <span key={i}>⭐</span>);
  };

  return (
    <div className="sports-easy-container">
      <header className="sports-easy-header">
        <h1>Sports - Easy</h1>
      </header>
      <div className="sports-easy-levels">
        {levels.map((level) => (
          <div key={level.number} className="level-card">
            <div className="level-number">{`Level ${level.number}`}</div>
            <div className="level-stars">{renderStars(level.stars)}</div>
          </div>
        ))}
      </div>
      <footer className="sports-easy-footer">
      <div className="bottom-nav">
        <Link to="/profile">
          <FontAwesomeIcon icon={faUser} />
        </Link>
        <Link to="/stats">
          <FontAwesomeIcon icon={faChartBar} />
        </Link>
        <Link to="/categories">
          <FontAwesomeIcon icon={faSignOutAlt} />
        </Link>
      </div>
      </footer>
    </div>
  );
}

export default SportsEasy;
