import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './SportsEasy.css';
import '../../css/BottomNav.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faChartBar, faSignOutAlt, faLock, faUnlock } from '@fortawesome/free-solid-svg-icons';


function SportsEasyLoggedIn() {

    const [currentLevel, setCurrentLevel] = useState(1); // Assumindo que o nível começa em 1
    const [score, setScore] = useState(0);
    const navigate = useNavigate();

    // Coloque aqui a lógica para buscar a pergunta do quiz

    const finishQuiz = async () => {
        const username = localStorage.getItem('username');
        const token = localStorage.getItem('access_token');

        if (!username || !token) {
            navigate('/login');
            return;
        }

        try {
            const response = await fetch('http://127.0.0.1:8000/scores', {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ level: currentLevel, score, username })
            });

            if (response.ok) {
                // Aqui você pode atualizar o estado para o próximo nível
                setCurrentLevel(prevLevel => prevLevel + 1);
                setScore(0); // Resetar a pontuação para o próximo nível
                // ... e/ou redirecionar para o componente que mostra os níveis, etc.
            } else {
                // Tratar erro
            }
        } catch (error) {
            console.error('Erro ao salvar pontuação:', error);
        }
    };

    // Renderizar o quiz ou a página de conclusão dependendo do estado

    return (
        <div>
            {/* Componente do quiz */}
            <button onClick={finishQuiz}>Finalizar Quiz</button>
        </div>
    );
}
export default SportsEasyLoggedIn;
