//#editprofile
import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import '../css/BottomNav.css'; 
import '../css/EditProfile.css'; 
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faChartBar, faSignOutAlt  } from '@fortawesome/free-solid-svg-icons';


function EditProfile() {
  const navigate = useNavigate();
  const [profile, setProfile] = useState({
    name: '',
    email: '',
    location: '',
    biography: ''
  });

  useEffect(() => {
    // Substitua 'your-api-url' pela URL da sua API para buscar os dados do utilizador
    const fetchData = async () => {
      try {
        const response = await fetch('your-api-url/user-data', {
          headers: {
            // Incluir o token JWT no cabeçalho de autorização se necessário
            'Authorization': `Bearer ${localStorage.getItem('access_token')}`
          }
        });
        if (!response.ok) {
          throw new Error('Falha ao buscar dados do utilizador');
        }
        const userData = await response.json();
        setProfile({
          name: userData.nome,
          email: userData.email,
          location: userData.location,
          biography: userData.biography
        });
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }
  
  , []);

  return (
    <div className="edit-profile-container">
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input type="text" id="name" name="name" value={profile.name} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input type="email" id="email" name="email" value={profile.email} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label htmlFor="location">Location</label>
          <input type="text" id="location" name="location" value={profile.location} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label htmlFor="biography">Biography</label>
          <textarea id="biography" name="biography" value={profile.biography} onChange={handleChange} />
        </div>
        <button type="submit" className="save-button">Save</button>
      </form>
      <div className="bottom-nav">
        <Link to="/profile">
          <FontAwesomeIcon icon={faUser} />
        </Link>
        <Link to="/stats">
          <FontAwesomeIcon icon={faChartBar} />
        </Link>
        <Link to="/categories">
          <FontAwesomeIcon icon={faSignOutAlt} />
        </Link>
      </div>
    </div>
  );
}

export default EditProfile;
