import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import '../css/BottomNav.css'; 
import '../css/EditProfile.css'; 

function EditProfile() {
  const navigate = useNavigate();
  const [profile, setProfile] = useState({
    name: 'André Pires',
    email: 'andrepires@email.com',
    location: 'Portugal',
    biography: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProfile(prevState => ({ ...prevState, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(profile);
    navigate('/profile'); 
  };

  return (
    <div className="edit-profile-container">
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input type="text" id="name" name="name" value={profile.name} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input type="email" id="email" name="email" value={profile.email} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label htmlFor="location">Location</label>
          <input type="text" id="location" name="location" value={profile.location} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label htmlFor="biography">Biography</label>
          <textarea id="biography" name="biography" value={profile.biography} onChange={handleChange} />
        </div>
        <button type="submit" className="save-button">Save</button>
      </form>
      <div className="bottom-nav">
        <Link to="/profile">Perfil</Link>
        <Link to="/stats">Estatísticas</Link>
        <Link to="/settings">Configurações</Link> 
      </div>
    </div>
  );
}

export default EditProfile;
