import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './SportsEasy.css';
import '../../css/BottomNav.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faChartBar, faSignOutAlt, faLock, faUnlock } from '@fortawesome/free-solid-svg-icons';


function SportsEasyLoggedIn() {
    const navigate = useNavigate();
    const [score, setScore] = useState(0); // A pontuação atual do usuário no quiz
    const [currentQuestion, setCurrentQuestion] = useState(null); // A pergunta atual
    const [currentLevel, setCurrentLevel] = useState(1); // O nível atual do usuário
    const [isQuizComplete, setIsQuizComplete] = useState(false); // Estado para verificar se o quiz foi completado

    // Função para buscar a próxima pergunta
    const fetchNextQuestion = async () => {
        // Implemente a lógica para buscar a próxima pergunta baseada no nível atual
    };

    // Efeito para buscar a primeira pergunta quando o componente é montado
    useEffect(() => {
        fetchNextQuestion();
    }, [currentLevel]); // Dependência no nível atual, para recarregar a pergunta quando o nível mudar

    // Função para tratar a seleção de resposta
    const handleAnswerSelection = (isCorrect) => {
        // Atualize a pontuação com base na resposta estar correta ou não
        if (isCorrect) {
            setScore(prevScore => prevScore + 1);
        }

        // Aqui você iria buscar a próxima pergunta ou finalizar o quiz se for a última pergunta
    };

    // Função para finalizar o quiz e enviar a pontuação para o backend
    const finishQuiz = async () => {
        const username = localStorage.getItem('username');
        const token = localStorage.getItem('access_token');

        if (!username || !token) {
            navigate('/login');
            return;
        }

        try {
            const response = await fetch('http://127.0.0.1:8000/scores', {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ level: currentLevel, score, username })
            });

            if (response.ok) {
                setIsQuizComplete(true);
                // Aqui você pode definir a lógica para atualizar o nível, se necessário
            } else {
                console.error('Falha ao salvar a pontuação');
            }
        } catch (error) {
            console.error('Erro ao salvar pontuação:', error);
        }
    };

    // Renderização condicional com base no estado do quiz
    if (isQuizComplete) {
        return (
            <div>
                <h1>Quiz Completo!</h1>
                <p>Sua pontuação: {score}</p>
                {/* Aqui você pode adicionar botões ou lógica para continuar para o próximo nível */}
            </div>
        );
    }

    return (
        <div>
            {currentQuestion && (
                <div>
                    <h2>{`Nível ${currentLevel}`}</h2>
                    <p>{currentQuestion.text}</p>
                    {/* Renderize as opções de resposta aqui e ligue-as a handleAnswerSelection */}
                    {currentQuestion.options.map((option, index) => (
                        <button key={index} onClick={() => handleAnswerSelection(option.isCorrect)}>
                            {option.text}
                        </button>
                    ))}
                </div>
            )}
            {/* Botão para finalizar o quiz manualmente, se desejado */}
            <button onClick={finishQuiz}>Finalizar Quiz</button>
        </div>
    );
}
export default SportsEasyLoggedIn;
