// Em QuestionEasy.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function QuestionEasy() {
  const [question, setQuestion] = useState(null);
  const { level } = useParams(); // Extrai o parâmetro de nível da URL

  useEffect(() => {
    const fetchQuestion = async () => {
      try {
        const response = await fetch(`http://127.0.0.1:8000/perguntas/?categoria=sports&nivel=easy&number=${level}`);
        if (!response.ok) {
          throw new Error('Problema ao buscar a pergunta');
        }
        const data = await response.json();
        setQuestion(data[0]); // Supondo que a API retorna uma lista e pegamos a primeira pergunta
      } catch (error) {
        console.error("Erro ao buscar pergunta:", error);
      }
    };

    fetchQuestion();
  }, [level]);

  if (!question) {
    return <div>Carregando pergunta...</div>;
  }

  return (
    <div>
      <h1>Pergunta do Nível {level}</h1>
      <h2>{question.pergunta}</h2>
      <ul>
        {question.respostas.map((resposta, index) => (
          <li key={index}>{resposta.texto}</li>
        ))}
      </ul>
      {question.imagem && <img src={question.imagem} alt="Imagem da pergunta" />}
    </div>
  );
}

export default QuestionEasy;
