// Profile.js
import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import '../css/BottomNav.css'; 
import '../css/Profile.css'; 
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faChartBar, faSignOutAlt } from '@fortawesome/free-solid-svg-icons';

const profileIconUrl = "https://cdn-icons-png.flaticon.com/512/149/149071.png"; // URL do ícone do perfil

function Profile() {
  const navigate = useNavigate();
  const [profile, setProfile] = useState({
    nome: '',
    email: '',
    // Adicione outros campos conforme necessário
  });

  const username = localStorage.getItem('username');
  const token = localStorage.getItem('access_token');

  useEffect(() => {
    const fetchProfile = async () => {
      if (username && token) {
        try {
          const response = await fetch(`http://127.0.0.1:8000/users/${username}`, {
            headers: new Headers({
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json'
            })
          });
          if (!response.ok) {
            throw new Error('Erro ao buscar dados do perfil.');
          }
          const userData = await response.json();
          setProfile({
            nome: userData.nome            // Adicione outros campos conforme o seu modelo de usuário
          });
        } catch (error) {
          console.error(error);
          navigate('/login'); // Redireciona para o login em caso de erro
        }
      } else {
        navigate('/login'); // Redireciona para o login se username ou token não estiverem presentes
      }
    };

    fetchProfile();
  }, [username, token, navigate]);

  const navigateToEditProfile = () => {
    navigate('/editProfile');
  };

  return (
    <div className="profile-container">
      <header className="profile-header">
        <img src={profileIconUrl} alt="Profile" className="profile-icon" />
        <h1>{profile.nome}</h1>
        <button onClick={navigateToEditProfile}>Edit Profile</button>
      </header>
      <div className="user-details">
        <p>userInfo.country</p>
        <p>userInfo.joinDate</p>
      </div>
      <div className="user-stats">
        <div>Respostas corretas: userInfo.correctAnswers</div>
        <div>Respostas erradas: userInfo.wrongAnswers</div>
        <div>Stars: userInfo.stars</div>
        <div>Classificação diária: userInfo.dailyRanking</div>
        <div>Classificação semanal: userInfo.weeklyRanking</div>
        <div>Classificação global: userInfo.globalRanking</div>
        
      </div>
      <div className="bottom-nav">
        <Link to="/profile">
          <FontAwesomeIcon icon={faUser} />
        </Link>
        <Link to="/stats">
          <FontAwesomeIcon icon={faChartBar} />
        </Link>
        <Link to="/categories">
          <FontAwesomeIcon icon={faSignOutAlt} />
        </Link>
      </div>
    </div>
    
  );
}

export default Profile;
